import React,{useState,useEffect} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './App.css';
import ScrollableList from './ScrollableList';
import CreateAPolicy from './CreateAPolicy';
import Button from 'react-bootstrap/Button';

const VAULT_TOKEN = "s.fbOgHTAbdPGAU45jjFs6HKoI"
const VAULT_URL = "http://localhost:8200"
function AssignPolicyToRole(props) {

  return (
      <div className="App" style={{textAlign:"left",marginLeft:15,marginBottom:50}}>
        <div style={{fontSize:30,color:"black",textAlign:"left",fontWeight:500}}>Assign Policies to Roles</div>
          <div>To assign a policy to a role, click on the policies then click on the role(s)</div>
          <div style={{display:"flex",flexDirection:"row"}}>
            <div>
            <h5>Policies</h5>          
            {Object.keys(props.policytoPolicyInfo).length > 0 && <ScrollableList list={props.policytoPolicyInfo} placeHolder={"Search by Policy or Policy Info"}f refreshData={props.refreshData} searchDictionaryValues={false}/>}
            </div>
            <div style={{display:"flex",flexDirection:"column",justifyContent:"space-around",textAlign:"center",alignItems:"center"}}>
              <p>Check policies on the left</p>
              <p>Then, check roles on the right</p>
                <div>
                <p>Then Submit!</p>
                <Button variant="primary" type="submit" style={{width:100,height:40}}>
                  Submit
                </Button>
              </div>
            </div>
            {Object.keys(props.roleToPolicies).length > 0 && <ScrollableList list={props.roleToPolicies} placeHolder={"Search by Role or Policy"}f refreshData={props.refreshData} searchDictionaryValues={true}/>}
          </div>
      </div>
  );
}

export default AssignPolicyToRole;
