import logo from './Nielsenlogo.jpg';
import React,{useState,useEffect} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import './App.css';
import ScrollableList from './ScrollableList';


const VAULT_TOKEN = "s.TEQoESp3xzHI6vllru90ijVJ"
const VAULT_URL = "http://localhost:8200"
function HomeScreen(props) {
  props = {token:VAULT_TOKEN}
  const [roleToPolicies,setRoleToPolicies] = useState({})
  const [policies,setPolicies] = useState([])


  useEffect(async () => {
        // Update the document title using the browser API
        // fetch(`policies?VAULT_TOKEN=`+ "s.yptIPktcOu8va4NTfzbEDjSq")
        // .then(response => response.json())
        // .then(data => {
        //   console.log( data)
        // })
        var roles = []
        var policies = []
        var roleToPTemp = {}
        fetch(`/auth?VAULT_TOKEN=` + props.token + `&auth_method=approle`, {
          method: "GET"
        }).then(response => response.json())
        .then(data => {roles = data})
        console.log("1")
        await fetch(`/policies?VAULT_TOKEN=` + props.token, {
          method: "GET"
        }).then(response => response.json())
        .then(data => {console.log(data); setPolicies(data)})
        // console.log("roles")
        // console.log(roles)
        console.log("2")
        // for(var i = 0; i < roles.length;++i){
          await fetch(`/auth?VAULT_TOKEN=` + props.token + `&auth_method=approle&role=`+roles[0], {
            method: "GET"
          }).then(response => response.json())
          .then(data => {roleToPTemp[roles[0]] = data;})
        // }
        console.log("3")
        // console.log("roleto")
        console.log(roleToPTemp)
        await setRoleToPolicies(roleToPTemp)
        // roleToPolicies = roleToPTemp
        // console.log("roletoP")
        console.log(roleToPolicies)
        
    },[]);
  return (
      <div className="App">
        <p style={{fontSize:50,color:"black",textAlign:"left",fontWeight:500}}>Policy Editor</p>
        <ScrollableList list={roleToPolicies} placeHolder={"Search by Role or Policy"}/>
      </div>
  );
}

export default HomeScreen;
