import Login from './Login';
import React from 'react';
import IconButton from '@material-ui/core/IconButton';

function ConfirmationMessage(props) {

  return (
    <div style={{position:"absolute",width:"100vw",height:"100vh",backgroundColor:"rgba(255,255,255,0.5)",display:"flex",justifyContent:"center",alignItems:"center"}}>
      <div style={{backgroundColor:"white",width:400,height:200}}>
        <div>Confirmation Message</div>
        <div>Are you sure you want to {props.message}</div>
        <div style={{display:"flex",flexDirection:"row"}}>
          <IconButton onClick={props.setAnswer(true)}>
            <div style={{backgroundColor:"green",width:50,height:50,borderRadius:25,fontSize:30,fontWeight:500}}>Yes</div>
          </IconButton>
          <IconButton onClick={props.setAnswer(false)}>

          </IconButton>
        </div>
      </div>

    </div>
  );
}

export default ConfirmationMessage;
