import logo from './Nielsenlogo.jpg';
import React,{useState,useEffect} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import './App.css';
import ScrollableList from './ScrollableList';


const VAULT_TOKEN = "s.ukPYVYDjAmyYqSNPVbj6CCbR"
const VAULT_URL = "http://localhost:8200"
function HomeScreen(props) {
  const [roleToPolicies,setrToP] = useState({})
  const [policies,setPolicies] = useState([])


  useEffect(async () => {
    // Update the document title using the browser API
    // fetch(`policies?VAULT_TOKEN=`+ "s.yptIPktcOu8va4NTfzbEDjSq")
    // .then(response => response.json())
    // .then(data => {
    //   console.log( data)
    // })
      await fetchData()
  });

  async function fetchData() {
    var roles = []
    var policies = []
    var roleToPTemp = {}
    fetch(`/auth?VAULT_TOKEN=` + props.token + `&auth_method=approle`, {
      method: "GET"
    }).then(response => { roles = response})
    await fetch(`/policies?VAULT_TOKEN=` + props.token, {
      method: "GET"
    }).then(response => { setPolicies(response)})
    console.log("roles")
    console.log(roles)
    roles.forEach(async (role) => {
      await fetch(`/auth?VAULT_TOKEN=` + props.token + `&auth_method=approle&role=`+role, {
        method: "GET"
      }).then(response => {roleToPTemp[role] = response;})
    })
    setrToP(roleToPTemp)
    console.log(roleToPolicies)
  }
  return (
      <div className="App">
        <p style={{fontSize:50,color:"black",textAlign:"left",fontWeight:500}}>Policy Editor</p>
        {/* <ScrollableList/> */}
      </div>
  );
}

export default HomeScreen;
