import Login from './Login';
import React from 'react';

import { useState, useEffect } from 'react';

function getWindowDimensions() {
  const { innerWidth: width, innerHeight: height } = window;
  return {
    width,
    height
  };
}

function useWindowDimensions() {
  const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());

  useEffect(() => {
    function handleResize() {
      setWindowDimensions(getWindowDimensions());
    }

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return windowDimensions;
}

function HomeTab() {
    const { height, width } = useWindowDimensions();
  return (
    <div style={{position:"absolute",display:"flex",flexDirection:"column",alignItems:"center",backgroundColor:"blue",width:"100vw" - 200,height:"95vh"}}>
        <div style={{position:"absolute",justifyContent:"center",alignItems:"center"}}>
            <div style={{fontSize:30,fontFamily:"system-ui"}}>Policy Manager</div>
            <div style={{fontSize:20,fontFamily:"sans-serif"}}>-Michael Askndafi-</div>

        </div>
    </div>
  );
}

export default HomeTab;
