import React,{useState,useEffect} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './App.css';
import ScrollableList from './ScrollableList';
import CreateAPolicy from './CreateAPolicy';
import Button from '@material-ui/core/Button';
import ReactLoading from 'react-loading'

const VAULT_TOKEN = "s.fbOgHTAbdPGAU45jjFs6HKoI"
const VAULT_URL = "http://localhost:8200"
function AssignPolicyToRole(props) {
    const [roleToPolicyCheckList,setrTPC] = React.useState([])
    const [policytoPolicyInfoCheckList,setPTPIC] = React.useState([])
    const [roleError,setRoleError] = React.useState("")
    const [policyError,setPolicyError] = React.useState("")
    const [loading,setLoading] = React.useState(false)
    async function handleAssign(event){
        event.preventDefault()
        if(policytoPolicyInfoCheckList.length == 0){setPolicyError("Must Select 1+ Policies");return;}else{setPolicyError("")}
        if(roleToPolicyCheckList.length == 0){setRoleError("Must Select 1+ Roles");return;}else{setRoleError("")}
        setLoading(true)
        // const mapToCapabilities = {0:'\\"create\\"',1:'\\"read\\"',2:'\\"update\\"',3:'\\"delete\\"'}
        const value = policytoPolicyInfoCheckList.toString()
        setrTPC([])
        setPTPIC([])
        for(var i = 0; i < roleToPolicyCheckList.length;++i){
            const requestOptions = {
                method:'POST',
                body:JSON.stringify({
                                    policies:value}),	
                headers: {
                    'Content-type': 'application/json; charset=UTF-8'
                }
            }
            await fetch('/auth?VAULT_TOKEN='+props.token+`&auth_method=approle&role=`+ roleToPolicyCheckList[i], requestOptions)
                .then(response => response.json())
                .then(data => console.log(data));
        }
        await props.refreshData();
        setLoading(false)
    }
    async function handleDisconnect(event){
        event.preventDefault()
        if(policytoPolicyInfoCheckList.length == 0){setPolicyError("Must Select 1+ Policies");return;}else{setPolicyError("")}
        if(roleToPolicyCheckList.length == 0){setRoleError("Must Select 1+ Roles");return;}else{setRoleError("")}
        setLoading(true)
        // const mapToCapabilities = {0:'\\"create\\"',1:'\\"read\\"',2:'\\"update\\"',3:'\\"delete\\"'}
        setrTPC([])
        setPTPIC([])
        for(var i = 0; i < roleToPolicyCheckList.length;++i){
            var newPolicies = props.roleToPolicies[roleToPolicyCheckList[i]].toString()
            for(var j = 0; j < policytoPolicyInfoCheckList.length;j++){
                const index = newPolicies.indexOf(policytoPolicyInfoCheckList[j])
                if(index !== -1){
                    newPolicies = newPolicies.replace(policytoPolicyInfoCheckList[j],"",1)
                    if(newPolicies[index] == ","){newPolicies.substr(0,index) + newPolicies.substr(index,newPolicies.length)}
                }
            }
            newPolicies.replace(value)
            const requestOptions = {
                method:'POST',
                body:JSON.stringify({
                                    policies:value}),	
                headers: {
                    'Content-type': 'application/json; charset=UTF-8'
                }
            }
            await fetch('/auth?VAULT_TOKEN='+props.token+`&auth_method=approle&role=`+ roleToPolicyCheckList[i], requestOptions)
                .then(response => response.json())
                .then(data => console.log(data));
        }
        await props.refreshData();
        setLoading(false)
    }

  return (
      <div style={{textAlign:"left",marginLeft:15,marginBottom:50}}>
        <div style={{fontSize:30,color:"black",textAlign:"left",fontWeight:500}}>Assign/Disconnect Policies to Roles</div>
          <div><i>To assign a policy to a role, checkbox on the policies then checkbox on the roles</i></div>
          <div style={{display:"flex",flexDirection:"row",marginTop:5}}>
            <div>
                <div style={{display:"flex",flexDirection:"row",alignItems:"center"}}><h5>Policies</h5>{policyError != "" && <div style={{color:"red",fontSize:15,marginBottom:5}}>{policyError}</div>}</div>       
                {Object.keys(props.policytoPolicyInfo).length > 0 && <ScrollableList list={props.policytoPolicyInfo} placeHolder={"Search by Policy or Policy Info"} setChecked={setPTPIC} searchDictionaryValues={false}/>}
            </div>
            <div>
            <div style={{display:"flex",flexDirection:"row",alignItems:"center"}}><h5>Roles</h5>{roleError != "" && <p style={{color:"red",fontSize:15,marginBottom:5}}>{roleError}</p>}</div>       
            {Object.keys(props.roleToPolicies).length > 0 && <ScrollableList list={props.roleToPolicies} placeHolder={"Search by Role or Policy"} setChecked={setrTPC} searchDictionaryValues={true}/>}
            </div>
          </div>
          <div style={{display:"flex",flexDirection:"row",justifyContent:"center",width:720}}>
          <Button color="secondary" variant="contained" style={{width:100,height:40,marginRight:30}} onClick={handleSubmit}>
                  Disconnect
          </Button>
          <Button color="primary" variant="contained" style={{width:100,height:40}} onClick={handleAssign}>
                  Assign
          </Button>
          {loading &&
          <div style={{position:"absolute",justifyContent:"absolute",alignItems:"center",marginTop:-200,marginLeft:20}}>
            <ReactLoading type={"spin"} color={"blue"} height={50} width={50} />
            <h6>Loading</h6>
          </div>}
          </div>

      </div>
  );
}

export default AssignPolicyToRole;
