import React,{useState,useEffect} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './App.css';
import ScrollableList from './ScrollableList';
import CreateAPolicy from './CreateAPolicy';
import Button from 'react-bootstrap/Button';

const VAULT_TOKEN = "s.fbOgHTAbdPGAU45jjFs6HKoI"
const VAULT_URL = "http://localhost:8200"
function HomeScreen(props) {
  props = {token:VAULT_TOKEN}
  const [roleToPolicies,setRoleToPolicies] = useState({})
  const [policytoPolicyInfo,setPTPI] = useState({})


  useEffect(async () => {
    // Update the document title using the browser API
    // fetch(`policies?VAULT_TOKEN=`+ "s.yptIPktcOu8va4NTfzbEDjSq")
    // .then(response => response.json())
    // .then(data => {
    //   console.log( data)
    // })
    await fetchData();
  },[])

  async function fetchData(){
    console.log("fetching data")
    var roles = []
    var policies = []
    var roleToPTemp = {}
    var policiesToPolicyInfo = {}
    fetch(`/auth?VAULT_TOKEN=` + props.token + `&auth_method=approle`, {
      method: "GET"
    }).then(response => response.json())
    .then(data => {roles = data})
    // console.log("1")
    await fetch(`/policies?VAULT_TOKEN=` + props.token, {
      method: "GET"
    }).then(response => response.json())
    .then(data => {console.log(data); policies = data})
    // console.log("roles")
    // console.log(roles)
    // console.log("2")
    for(var i = 0; i < roles.length;++i){
      await fetch(`/auth?VAULT_TOKEN=` + props.token + `&auth_method=approle&role=`+roles[i], {
        method: "GET"
      }).then(response => response.json())
      .then(data => {roleToPTemp[roles[i]] = data;})
    }

    for(var i = 0; i < policies.length;++i){
      await fetch(`/policies?VAULT_TOKEN=` + props.token + `&policyLabel=`+policies[i], {
        method: "GET"
      }).then(response => response.json())
      .then(data => {policiesToPolicyInfo[policies[i]] = data;})
    }

    // console.log("3")
    // // console.log("roleto")
    // console.log(roleToPTemp)
    await setRoleToPolicies(roleToPTemp)
    await setPTPI(policiesToPolicyInfo)
    // roleToPolicies = roleToPTemp
    // console.log("roletoP")
    console.log(policiesToPolicyInfo)
    
  }
  // useEffect(() => {console.log("here");console.log(policytoPolicyInfo)},[policytoPolicyInfo] )
  return (
      <div className="App" style={{textAlign:"left",marginLeft:15,marginBottom:50}}>
            {policytoPolicyInfo.length > 0 && <p style={{visibility:"hidden"}}>{policytoPolicyInfo[Object.keys(policytoPolicyInfo)[0]]}</p>}

        <p style={{fontSize:50,color:"black",textAlign:"left",fontWeight:500}}>Policy Editor</p>
        <CreateAPolicy token={props.token} refreshData={fetchData}/>
        <p style={{fontSize:30,color:"black",textAlign:"left",fontWeight:500}}>Assign Policies to Roles</p>
        <p>To assign a policy to a role, click on the policies then click on the role(s)</p>
        <div style={{display:"flex",flexDirection:"row"}}>          
        {Object.keys(policytoPolicyInfo).length > 0 && <ScrollableList list={policytoPolicyInfo} placeHolder={"Search by Policy or Policy Info"}f refreshData={fetchData} searchDictionaryValues={false}/>}
          <div style={{display:"flex",flexDirection:"column",justifyContent:"space-around",textAlign:"center",alignItems:"center"}}>
            <p>Check policies on the left</p>
            <p>Then, check roles on the right</p>
              <div>
              <p>Then Submit!</p>
              <Button variant="primary" type="submit" style={{width:100,height:40}}>
                Submit
              </Button>
            </div>
          </div>
          {Object.keys(roleToPolicies).length > 0 && <ScrollableList list={roleToPolicies} placeHolder={"Search by Role or Policy"}f refreshData={fetchData} searchDictionaryValues={true}/>}
          <div style={{display:"flex",flexDirection:"row",justifyContent:"end",alignItems:"end"}}>
          <Button variant="primary" type="submit" style={{width:100,height:40,marginTop:"auto"}}>
            Submit
          </Button>
          </div>
        </div>
      </div>
  );
}

export default HomeScreen;
