import logo from './Nielsenlogo.jpg';
import React,{useState,useStyles} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import './App.css';

import {List,ListItem,ListItemAvatar,Avatar,ListItemText,ListItemSecondaryAction,Checkbox} from '@material-ui/core';
import { FixedSizeList } from 'react-window';
import { coerce } from 'yargs';

export default function ScrollableList() {
 const items = [{'id': 'id1','message':'message1'},{'id': 'id2','message':'message2'},{'id': 'id3','message':'message3'},{'id': 'id1','message':'message1'},{'id': 'id2','message':'message2'},{'id': 'id3','message':'message3'},{'id': 'id1','message':'message1'},{'id': 'id2','message':'message2'},{'id': 'id3','message':'message3'},{'id': 'id1','message':'message1'},{'id': 'id2','message':'message2'},{'id': 'id3','message':'message3'}]
 const classes = useStyles();
 const [checked, setChecked] = React.useState([1]);

 const handleToggle = (value) => () => {
   const currentIndex = checked.indexOf(value);
   const newChecked = [...checked];

   if (currentIndex === -1) {
     newChecked.push(value);
   } else {
     newChecked.splice(currentIndex, 1);
   }

   setChecked(newChecked);
 };
 
 return (
      <div style={{backgroundColor:"blue",justifyContent:"center",alignItems:"center"}}>
      {/* <FixedSizeList height={400} width={300} itemSize={46} itemCount={200}> */}
      <List dense className={classes.root}>
      {[0, 1, 2, 3].map((value) => {
        const labelId = `checkbox-list-secondary-label-${value}`;
        return (
          <ListItem key={value} button>
            <ListItemAvatar>
              <Avatar
                alt={`Avatar n°${value + 1}`}
                src={`/static/images/avatar/${value + 1}.jpg`}
              />
            </ListItemAvatar>
            <ListItemText id={labelId} primary={`Line item ${value + 1}`} />
            <ListItemSecondaryAction>
              <Checkbox
                edge="end"
                onChange={handleToggle(value)}
                checked={checked.indexOf(value) !== -1}
                inputProps={{ 'aria-labelledby': labelId }}
              />
            </ListItemSecondaryAction>
          </ListItem>
        );
      })}
    </List>
      {/* </FixedSizeList> */}
      </div>
  );
}
