import Login from './Login';
import React from 'react';
import Circle from './Circle';
import {AiFillDelete,AiOutlineFileAdd,AiOutlineRead} from "react-icons/ai";
import { BiLink,BiUnlink,BiEdit } from "react-icons/bi";

import { useState, useEffect } from 'react';
function getWindowDimensions() {
  const { innerWidth: width, innerHeight: height } = window;
  return {
    width,
    height
  };
}

function useWindowDimensions() {
  const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());

  useEffect(() => {
    function handleResize() {
      setWindowDimensions(getWindowDimensions());
    }

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return windowDimensions;
}
function HomeTab() {
  const itemsFirstRow = 3;
  const itemsSecondRow = 2;
  const sizeOfCircle = 250;
  const { height, width } = useWindowDimensions();
  return (
    <div style={{position:"absolute",display:"flex",flexDirection:"column",width:"95vw",height:"95vh"}}>
        <div style={{display:"flex",flexDirection:"row",justifyContent:"space-around"}}>
        <Circle diameter={(width/4) - 60} icon={<AiOutlineFileAdd style={{width:100,height:100}}/>} text={"Create a Policy"}/>
        <Circle diameter={(width/4) - 60} icon={<AiFillDelete style={{width:100,height:100}}/>} text={"Delete a Policy"}/>
        <Circle diameter={(width/4) - 60} icon={<AiOutlineRead style={{width:100,height:100}}/>} text={"Read a Policy"}/>
        <Circle diameter={(width/4) - 60} icon={<BiEdit style={{width:100,height:100}}/>} text={"Update a Policy"}/>
        </div>
        <div style={{display:"flex",marginTop:-30,flexDirection:"row",justifyContent:"center"}}> 
          <Circle diameter={sizeOfCircle} icon={<BiUnlink style={{width:100,height:100}}/>} text={"Disconnect Policy From Role"}/>
          <div style={{fontSize:width /10,fontWeight:600,fontFamily:"Helvetica",marginLeft:100,marginRight:100,overflowWrap:"normal", width:200,display:"flex",justifyContent:"center",alignItems:"center",textAlign:"center"}}>Policy Manager</div>
          <Circle diameter={sizeOfCircle} icon={<BiLink style={{width:100,height:100}}/>} text={"Connect a Policy From Role"}/>
        </div>

    </div>
  );
}

export default HomeTab;
