import React,{useState,useEffect} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './App.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

const VAULT_TOKEN = "s.TEQoESp3xzHI6vllru90ijVJ"
const VAULT_URL = "http://localhost:8200"
function CreateAPolicy(props) {
  const [nameOfPolicy,setName] = React.useState("")
  const [secretPath,setSecretPath] = React.useState("")
  const [nameError,setNameError] = React.useState("")
  const [policyError,setPolicyError] = React.useState("")
  function handleSubmit(event){
        event.preventDefault()
        if(nameOfPolicy == ""){setNameError("There is an issue with the name")}

  }

  return (
      <div >
        <p style={{fontSize:30,color:"black",textAlign:"left",fontWeight:500}}>Create/Update a Policy</p>
        <Form onSubmit={(event) => handleSubmit(event)}>
            <Form.Group controlId="formToken" onChange={(event) => setName(event.target.value)}style={{display:"flex",flexDirection:"column"}}>
                <Form.Label>
                <div style={{overflow:"hidden",whiteSpace:"nowrap",color:"black",marginRight:15}}>
                Name of Policy: 
                </div>
                </Form.Label>
                <Form.Control placeholder="Enter Token" />
            </Form.Group>
            <Form.Group controlId="formToken" onChange={(event) => setSecretPath(event.target.value)}style={{display:"flex",flexDirection:"column"}}>
                <Form.Label>
                <div style={{overflow:"hidden",whiteSpace:"nowrap",color:"black",marginRight:15}}>
                Secret Path: 
                </div>
                </Form.Label>
                <Form.Control placeholder="e.g. secret/foo/*" />
            </Form.Group>
            <Button variant="primary" type="submit">
                Submit
            </Button>
            {error == true && <p style={{color:"red",fontSize:15}}>There was an error with the token. Try again.</p>}
        </Form>
      </div>
  );
}

export default CreateAPolicy;
