import logo from './Nielsenlogo.jpg';
import React,{useState} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import HomeScreen from './HomeScreen';
import './App.css';


function Login() {
  const [token,setToken] = useState("George")
  const [error,setError] = useState("Initialized")

  function handleSubmit(event){
    // alert("hi")
    // console.log(event)
    event.preventDefault();
    fetch(`/api/policies?VAULT_TOKEN=`+ token)
    .then(response => response.json())
    .then(data => {
      // console.log( data)
        setError(data['message'] !== undefined)
    })
  }
  return (
      <div className="App">
        {error == "Initalized" || error == true ? 
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p style={{color:"black",fontSize:"40px"}}>
            Policy Editor
          </p>
          <Form onSubmit={(event) => handleSubmit(event)}>
            <Form.Group controlId="formToken" onChange={(event) => setToken(event.target.value)}style={{display:"flex",flexDirection:"row"}}>
              <Form.Label>
              <div style={{overflow:"hidden",whiteSpace:"nowrap",color:"black",marginRight:15}}>
                Vault Token: 
              </div>
              </Form.Label>
              <Form.Control placeholder="Enter Token" />
            </Form.Group>
            <Button variant="primary" type="submit">
              Submit
            </Button>
            {error == true && <p style={{color:"red",fontSize:15}}>There was an error with the token. Try again.</p>}
          </Form>

        </header>
        : 
        <HomeScreen token={token}/>}
      </div>
  );
}

export default Login;
