import React, { useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Checkbox from '@material-ui/core/Checkbox';
import Avatar from '@material-ui/core/Avatar';
import SearchBar from "material-ui-search-bar";

import { ListSubheader } from '@material-ui/core';
const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    maxWidth: 360,
    backgroundColor: theme.palette.background.paper,
    overflow:"auto",
    maxHeight:300,
    marginTop:-35
  },
  noResults:{
    width: '100%',
    maxWidth: 360,
    backgroundColor: theme.palette.background.paper,
    maxHeight:300,
    marginTop:10
  },
  orange:{
    backgroundColor:"blue"
  }
}));
const top100Films = [
  { title: 'The Shawshank Redemption', year: 1994 },
  { title: 'The Godfather', year: 1972 },
  { title: 'The Godfather: Part II', year: 1974 },
  { title: 'The Dark Knight', year: 2008 },
  { title: '12 Angry Men', year: 1957 },
  { title: "Schindler's List", year: 1993 },
  { title: 'Pulp Fiction', year: 199 }]


export default function CheckboxListSecondary(props) {
  const classes = useStyles();
  const [checked, setChecked] = React.useState([]);
  const [searchValue,setSearchValue] = React.useState("");
  const x = Object.keys(props.list)
  console.log(x)
  const [list,setList] = React.useState(x)
  useEffect(() => {
    setList(Object.keys(props.list))
  })
  const handleToggle = (value) => () => {
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setChecked(newChecked);
  };

  function searchResults(searchValue){
    if(searchValue == ""){setList(props.list); return;}
    const matches = []
    const keys = Object.keys(props.list)
    for(var i = 0; i < keys.length;i++){
      if(keys[i].includes(searchValue)){
        matches.push(keys[i])
      }
    };
    setList(matches)
  };

  return (
    <div style={{border:'1.5px solid gray',width:360}}>
  <SearchBar
    value={searchValue}
    placeHolder={props.placeHolder}
    onChange={(newValue) => { setSearchValue(newValue)}}
    onRequestSearch={() => searchResults(searchValue)}
  />
    {list.length > 0 && <p style={{visibility:"hidden"}}>{list[0]}</p>}
    
    <List className={list.length == 0 ? classes.noResults : classes.root} subheader={<li />}>
      {list.length == 0 && 
      <ListItem style={{justifyContent:"center",textAlign:"center"}}>
      <ListItemText primary={"No Results"} />
      </ListItem>}
      {list.map((key) => {
        console.log("value")
        console.log(key)
        const labelId = `checkbox-list-secondary-label-${key}`;
        return (
          <ListItem key={key} button>
            <ListItemAvatar>
              <Avatar className={classes.orange}>B</Avatar>
            </ListItemAvatar>
            <ListItemText id={labelId} primary={props.list[key]} />
            <ListItemSecondaryAction>
              <Checkbox
                edge="end"
                onChange={handleToggle(key)}
                checked={checked.indexOf(key) !== -1}
                inputProps={{ 'aria-labelledby': labelId }}
              />
            </ListItemSecondaryAction>
          </ListItem>
        );
      })}
    </List>
    </div>
  );
}
