import React, { useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Checkbox from '@material-ui/core/Checkbox';
import Avatar from '@material-ui/core/Avatar';
import SearchBar from "material-ui-search-bar";
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';

import { ListSubheader } from '@material-ui/core';
const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    width: 360,
    backgroundColor: theme.palette.background.paper,
    overflow:"auto",
    height:300,
    marginTop:-35
  },
  noResults:{
    width: '100%',
    width: 360,
    backgroundColor: theme.palette.background.paper,
    height:300,
    marginTop:10
  },
  blue:{
    backgroundColor:"blue"
  }
}));


export default function CheckboxListSecondary(props) {
  const classes = useStyles();
  const [checked, setChecked] = React.useState([]);
  const [searchValue,setSearchValue] = React.useState("");
  const [list,setList] = React.useState([])
  useEffect(() => {setList(Object.keys(props.list));setChecked([])},[props.list] )
  const handleToggle = (value) => () => {
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setChecked(newChecked);
    props.setChecked(newChecked)
  };

  function searchResults(searchValue){
    if(searchValue == ""){setList(Object.keys(props.list)); return;}
    const matches = []
    const keys = Object.keys(props.list)
    for(var i = 0; i < keys.length;i++){
      if(keys[i].includes(searchValue)){
        matches.push(keys[i])
      }
    };
    const values = Object.values(props.list)
    for(var i = 0; i < values.length;i++){
      if(props.searchDictionaryValues){
        for(var j = 0;j < values[i].length;j++){
          if(values[i][j].includes(searchValue)){
            matches.push(keys[i])
            break
          }
        }
      }else if(values[i].rules.includes(searchValue)){
        matches.push(keys[i])
      }
    };  
    setList(matches)
  };

  function parsePath(path){
    var start = path.indexOf("\"")
    var end = path.indexOf("\"",start + 1)

    var newPath = "path: " + path.substring(start,Math.min(end - 1 - start, 30) + start) +"\"" +((end - 1 - start) > 30 ? "..." : "") + " permissions:" + (path.indexOf("create") != -1 ? " Create": "") + (path.indexOf("update") != -1 ? " Update" : "")+ (path.indexOf("delete") != -1 ? " Delete" : "")+ (path.indexOf("read") != -1 ? " Read": "")
    return newPath
  };

  return (
    <div style={{border:'1.5px solid gray',width:365}}>
  <SearchBar
    value={searchValue}
    placeholder={props.placeHolder}
    onChange={(newValue) => { setSearchValue(newValue)}}
    onRequestSearch={() => searchResults(searchValue)}
    onCancelSearch={() => {setList(Object.keys(props.list));setSearchValue("")}}
  />
    {list.length > 0 && <p style={{visibility:"hidden"}}>{list[0]}</p>}
    
    <List className={list.length == 0 ? classes.noResults : classes.root} subheader={<li />}>
      {list.length == 0 ?
      <ListItem style={{justifyContent:"center",textAlign:"center"}}>
      <ListItemText primary={"No Results"} />
      </ListItem> :
        list.map((key) => {
        const labelId = `checkbox-list-secondary-label-${key}`;
        return (
          <ListItem key={key} onClick={handleToggle(key)} >
            <ListItemAvatar>
              <Avatar className={classes.blue}>{key.substring(0,1)}</Avatar>
            </ListItemAvatar>
            {/* {!props.searchDictionaryValues && console.log("f"), console.log(props.list)} */}
            <ListItemText id={labelId} primary={<div style={{}}>{key}<ListItem>                    <IconButton edge="end" aria-label="delete">
                      <DeleteIcon />
                    </IconButton></ListItem></div>} secondary={(props.searchDictionaryValues ? "Policies: " + props.list[key].map((v) => v) : parsePath(props.list[key].rules))} />
            <ListItemSecondaryAction>
              <Checkbox
                edge="end"
                onChange={handleToggle(key)}
                checked={checked.indexOf(key) !== -1}
                inputProps={{ 'aria-labelledby': labelId }}
              />
            </ListItemSecondaryAction>
          </ListItem>
        );
      })}
    </List>
    </div>
  );
}
